function calculateProfile(answers: Answer[]): Profile {
  const scores = initializeScores();
  answers.forEach(a => {
    Object.entries(a.scoring).forEach(([key, value]) => {
      scores[key] += value;
    });
  });

  const elementWinner = getMaxKey(scores, ['fire', 'water', 'air', 'earth']);
  const modalityWinner = getMaxKey(scores, ['cardinal', 'fixed', 'mutable']);
  const orientationWinner = getMaxKey(scores, ['solar', 'lunar']);

  const profileKey = `${orientationWinner}_${modalityWinner}_${elementWinner}`;
  return profiles.find(p => p.id === profileKey) || fallbackProfile;
}
